const apiKey = "b7d3bda1e0818bb4b407638b5d0b4006";
const imgPath = "https://image.tmdb.org/t/p/w1280";

const input = document.querySelector("input");
const btn = document.querySelector("button");
const mainGridTitle = document.querySelector("h1");
const mainGrid = document.querySelector(".favorites .moviesGrid");

const addKey = (e) => {
  addSearchedMoviesToDom();
  if (e.which === 13) {
    addSearchedMoviesToDom();
    input.value = "";
  }
};

const addSearchedMoviesToDom = async () => {
  const data = await getMovies(input.value);
  console.log(data);

  mainGridTitle.innerText = "Search Results...";
  mainGrid.innerHTML = data
    .map((e) => {
      return `
          <div class="card" data-id="${e.id}">
              <div class="img">
                  <img
                      src="${imgPath + e.poster_path}"/>
              </div>
              <div class="info">
                  <h2>${e.title}</h2>
                  <div class="singleInfo">
                      <span>Rate:</span>
                      <span>${e.vote_average}</span>
                  </div>
                  <div class="singleInfo">
                      <span>Release Date:</span>
                      <span>${e.release_date}</span>
                  </div>
                  </div>
              </div>
    `;
    })
    .join("");
};

function App() {
  return (
    <div>
      <input type="text"></input>
    </div>
  );
}

export default App;
