import { useState } from "react";
import style from "./styles/style.css";

const apiKey = "b7d3bda1e0818bb4b407638b5d0b4006";
const imgPath = "https://image.tmdb.org/t/p/w1280";

function App() {
  const [inputValue, setInputValue] = useState("");
  const [Movies, setMovies] = useState([]);

  const getMovies = async () => {
    const resp = await fetch(
      `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${inputValue}&language=zh-TW`
    );
    const respData = await resp.json();
    setMovies(respData.results);
  };

  const addSearchedMoviesToDom = async () => {};

  return (
    <div>
      <div>
        <input
          type="text"
          onChange={(e) => {
            setInputValue(e.target.value);
          }}
        ></input>
        <button
          onClick={() => {
            getMovies();
          }}
        >
          Search
        </button>
      </div>
      {Movies[0] ? Movies[0].title : ""}
    </div>
  );
}

export default App;
